create table <?= DB_SCHEMA ?>.matrix_activities (
	_id int not null auto_increment comment 'Identificador',
	_description Varchar(512) null comment 'Descripción',
	_workers int not null comment 'Responsables de planificación',
	_coworkers Varchar(1024) not null comment 'Factores críticos y entes internos cooperantes MEP que favorecen la implementación',
	_indicator int not null comment 'Indicador',
	_milestone int not null comment 'Hitos o resultado',
	primary key (_id),
	unique key (_description),
	foreign key (_workers) references <?= DB_SCHEMA ?>.security_groups(_id),
	foreign key (_indicator) references <?= DB_SCHEMA ?>.matrix_indicators(_id),
	foreign key (_milestone) references <?= DB_SCHEMA ?>.matrix_milestones(_id)
)
engine = InnoDB
comment = 'Actividades';


