create table <?= DB_SCHEMA ?>.matrix_milestones (
	_id int not null auto_increment comment 'Identificador',
	_name Varchar(64) not null comment 'Nombre',
	_description Varchar(1024) null comment 'Descripción',
	_goal int not null comment 'Objetivo',
	primary key (_id),
	unique key uk_milestones_goals_name (_goal, _name),
	foreign key (_goal) references <?= DB_SCHEMA ?>.matrix_goals(_id)
)
engine = InnoDB
comment = 'Hitos o resultados';


